mapper.insert:
insert into bake_circle (circle_id, time, description, 
  resources, praise, comments
  )
values (#{circleId,jdbcType=INTEGER}, #{time,jdbcType=DATE}, #{description,jdbcType=VARCHAR}, 
  #{resources,jdbcType=VARCHAR}, #{praise,jdbcType=VARCHAR}, #{comments,jdbcType=VARCHAR}
  )

mapper.select:
select circle_id, time, description, resources, praise, comments
from bake_circle
where circle_id = #{circleId,jdbcType=INTEGER}
