mapper.insert:
insert into comment (comment_id, user_id, comment_time, content
  )
values (#{commentId,jdbcType=INTEGER}, #{userId,jdbcType=INTEGER}, #{commentTime,jdbcType=DATE}, #{content,jdbcType=VARVHAR}
  )

mapper.update:
update comment
set user_id = #{userId,jdbcType=INTEGER},
  comment_time = #{commentTime,jdbcType=DATE},
  content = #{content,jdbcType=VARVHAR}
where comment_id = #{commentId,jdbcType=INTEGER}

mapper.select:
select comment_id, user_id, comment_time,content
from comment
where comment_id = #{commentId,jdbcType=INTEGER}
